import React, { useEffect, useState, useCallback } from 'react';
import { FaCheckCircle, FaClock, FaMedal, FaYoutube, FaArrowRight } from 'react-icons/fa';
import axios from 'axios';

const getYouTubeVideoId = (url) => {
  try {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  } catch (error) {
    console.error('Error extracting YouTube video ID:', error);
    return null;
  }
};

const AssessmentResults = ({ submissions }) => {
  const [suggestedCourses, setSuggestedCourses] = useState([]);

  const fetchSuggestedCourses = useCallback(async () => {
    try {
      // Get low score submissions
      const lowScoreSubmissions = submissions.filter(sub => 
        sub.score < 60 && sub.assessment?.engineeringField
      );

      if (lowScoreSubmissions.length > 0) {
        const token = localStorage.getItem('token');

        // Get unique fields
        const fields = [...new Set(
          lowScoreSubmissions.map(sub => sub.assessment.engineeringField)
        )];

        const response = await axios.get('/api/dashboard/categories/beginner-courses', {
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          params: { fields: fields.join(',') }
        });

        setSuggestedCourses(response.data);
      } else {
        setSuggestedCourses([]);
      }
    } catch (error) {
      console.error('Error fetching courses:', error);
      setSuggestedCourses([]);
    }
  }, [submissions]);

  useEffect(() => {
    if (submissions && submissions.length > 0) {
      console.log('Fetching suggested courses for submissions:', submissions.length);
      fetchSuggestedCourses();
    }
  }, [fetchSuggestedCourses, submissions]);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const getStatusColor = (score) => {
    if (score >= 70) return 'text-green-600';
    if (score >= 50) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getProgressBarColor = (score) => {
    if (score >= 70) return 'bg-green-500';
    if (score >= 50) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  const getLevelBadgeStyle = (level) => {
    switch (level?.toLowerCase()) {
      case 'beginner':
        return 'bg-green-100 text-green-800';
      case 'intermediate':
        return 'bg-blue-100 text-blue-800';
      case 'expert':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      {/* Assessment Results Section */}
      <div className="mt-8 bg-white rounded-lg shadow">
        <div className="p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-6">Assessment History</h2>

          {submissions.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              No assessment history available
            </div>
          ) : (
            <div className="space-y-4">
              {submissions.map((submission) => (
                <div key={submission._id} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-medium text-gray-900">
                        {submission.assessment?.title || 'Deleted Assessment'}
                      </h3>
                      <div className="mt-2 flex items-center space-x-4 text-sm text-gray-500">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          getLevelBadgeStyle(submission.assessment?.level)
                        }`}>
                          {submission.assessment?.level || 'Unknown Level'}
                        </span>
                        <span className="flex items-center">
                          <FaClock className="mr-1.5 h-4 w-4 text-gray-400" />
                          {Math.round(submission.timeSpent / 60)}m
                        </span>
                        <span>{submission.correctAnswers}/{submission.totalQuestions} correct</span>
                        <span>Attempt #{submission.attemptNumber || 1}</span>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className={`text-lg font-semibold ${getStatusColor(submission.score)}`}>
                        {submission.score}%
                      </div>
                      <div className="text-sm text-gray-500">
                        {formatDate(submission.createdAt)}
                      </div>
                    </div>
                  </div>

                  {/* Progress Bar */}
                  <div className="mt-4">
                    <div className="w-full bg-gray-100 rounded-full h-2">
                      <div
                        className={`h-2 rounded-full ${getProgressBarColor(submission.score)}`}
                        style={{ width: `${submission.score}%` }}
                      />
                    </div>
                  </div>

                  {/* Engineering Field */}
                  {submission.assessment?.engineeringField && (
                    <div className="mt-4 text-sm text-gray-600">
                      Field: {submission.assessment.engineeringField}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Suggested Courses Section - Add debug info */}
      {console.log('Current suggested courses:', suggestedCourses)}
      {suggestedCourses.length > 0 ? (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-lg font-medium text-gray-900 mb-4">
            Recommended Learning Resources
          </h2>
          <div className="space-y-4">
            {suggestedCourses.map((course, index) => (
              <div 
                key={index}
                className="border rounded-lg p-4 hover:shadow-md transition-shadow duration-200"
              >
                <div className="flex items-start space-x-4">
                  <div className="flex-shrink-0">
                    <div className="w-32 h-20 bg-gray-100 rounded-lg overflow-hidden relative">
                      <img
                        src={`https://img.youtube.com/vi/${getYouTubeVideoId(course.videoResources[0].url)}/mqdefault.jpg`}
                        alt={course.name}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute inset-0 bg-black bg-opacity-10 flex items-center justify-center">
                        <FaYoutube className="h-8 w-8 text-red-600" />
                      </div>
                    </div>
                  </div>
                  <div className="flex-1">
                    <h3 className="text-md font-medium text-gray-900">
                      {course.name}
                    </h3>
                    <p className="text-sm text-gray-500 mt-1">
                      {course.description}
                    </p>
                    <div className="mt-2 flex items-center justify-between">
                      <span className="text-xs text-gray-500">
                        Duration: {course.recommendedDuration} weeks
                      </span>
                      <a
                        href={course.videoResources[0].url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center text-sm text-blue-600 hover:text-blue-800"
                      >
                        Start Learning
                        <FaArrowRight className="ml-1 h-3 w-3" />
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow p-6">
          <p className="text-gray-500 text-center">
            No learning resources suggested at this time.
            {submissions.length > 0 && ' Try completing more assessments to get personalized recommendations.'}
          </p>
        </div>
      )}
    </div>
  );
};

export default AssessmentResults;
