import React, { useState, useEffect } from 'react';
import { FaBook, FaCertificate, FaChartLine, FaCalendar, FaClock } from 'react-icons/fa';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import AssessmentResults from './AssessmentResults';

const AssessmentCard = ({ assessment, submissions }) => {
  const navigate = useNavigate();
  const submission = submissions[assessment._id];

  const getStatusDisplay = () => {
    if (!submission) return { text: 'Not Started', color: 'yellow' };
    if (submission.status === 'completed') {
      return { text: `Last Score: ${submission.score}%`, color: 'green' };
    }
    return { text: `Last Score: ${submission.score}%`, color: 'orange' };
  };

  const handleStartAssessment = () => {
    if (submission) {
      const confirmRetake = window.confirm(
        'You have already completed this assessment. Would you like to retake it? Your previous score will be kept in history.'
      );
      if (!confirmRetake) return;
    }
    navigate(`/assessment/${assessment._id}`);
  };

  const status = getStatusDisplay();

  return (
    <div className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200 p-6">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">{assessment.title}</h3>
          <div className="flex items-center mt-2 space-x-4 text-sm text-gray-500">
            <span className="flex items-center">
              <span className={`w-2 h-2 rounded-full mr-2 ${
                assessment.level === 'beginner' ? 'bg-green-500' :
                assessment.level === 'intermediate' ? 'bg-yellow-500' : 'bg-red-500'
              }`}></span>
              {assessment.level.charAt(0).toUpperCase() + assessment.level.slice(1)}
            </span>
            <span className="flex items-center">
              <FaClock className="mr-1.5 h-4 w-4 text-gray-400" />
              {assessment.duration} mins
            </span>
            <span>{assessment.questions.length} questions</span>
          </div>
        </div>
        <button
          onClick={handleStartAssessment}
          className={`px-4 py-2 text-sm rounded-md transition-colors duration-200 ${
            submission 
              ? 'bg-green-600 hover:bg-green-700 text-white'
              : 'bg-blue-600 hover:bg-blue-700 text-white'
          }`}
        >
          {submission ? 'Retake Assessment' : 'Start Assessment'}
        </button>
      </div>
      <div className="mt-4 pt-4 border-t border-gray-100">
        <div className="flex justify-between text-sm">
          <span className="text-gray-500">Status</span>
          <span className={`font-medium text-${status.color}-600`}>
            {status.text}
          </span>
        </div>
        {submission && (
          <div className="mt-2 text-sm text-gray-500">
            <div className="flex justify-between">
              <span>Last Attempt</span>
              <span>{new Date(submission.createdAt).toLocaleDateString()}</span>
            </div>
            <div className="flex justify-between">
              <span>Time Spent</span>
              <span>{Math.round(submission.timeSpent / 60)} minutes</span>
            </div>
            <div className="flex justify-between mt-1">
              <span>Score History</span>
              <span>{submission.correctAnswers} / {submission.totalQuestions}</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const StudentDashboard = () => {
  const [assessments, setAssessments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    totalAssessments: 0,
    completedAssessments: 0,
    averageScore: 0,
    timeSpent: 0
  });
  const [submissions, setSubmissions] = useState({});
  const [allSubmissions, setAllSubmissions] = useState([]);
  const location = useLocation();

  useEffect(() => {
    fetchAssessments();
    fetchStats();
    fetchSubmissions();
  }, []);

  useEffect(() => {
    if (location.state?.message) {
      alert(location.state.message);
      fetchAssessments();
      fetchStats();
      fetchSubmissions();
      window.history.replaceState({}, document.title);
    }
  }, [location]);

  const fetchAssessments = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/student/assessments', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAssessments(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch assessments');
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/student/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch stats:', err);
    }
  };

  const fetchSubmissions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/student/submissions', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Store all submissions for history
      setAllSubmissions(response.data);
      
      // Create map of latest submissions for assessment cards
      const submissionMap = {};
      response.data.forEach(sub => {
        if (!submissionMap[sub.assessment._id] || 
            new Date(sub.createdAt) > new Date(submissionMap[sub.assessment._id].createdAt)) {
          submissionMap[sub.assessment._id] = sub;
        }
      });
      setSubmissions(submissionMap);
    } catch (err) {
      console.error('Failed to fetch submissions:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-gray-900">Student Dashboard</h1>
            </div>
            <div className="flex items-center">
              <span className="text-gray-700 mr-2">
                {localStorage.getItem('userName')}
              </span>
              <button
                onClick={() => {
                  localStorage.clear();
                  window.location.href = '/login';
                }}
                className="ml-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-blue-500 bg-opacity-10">
                <FaBook className="h-6 w-6 text-blue-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-500">Total Assessments</p>
                <p className="text-2xl font-semibold text-gray-700">{stats.totalAssessments}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-green-500 bg-opacity-10">
                <FaCertificate className="h-6 w-6 text-green-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-500">Completed</p>
                <p className="text-2xl font-semibold text-gray-700">{stats.completedAssessments}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-purple-500 bg-opacity-10">
                <FaChartLine className="h-6 w-6 text-purple-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-500">Average Score</p>
                <p className="text-2xl font-semibold text-gray-700">{stats.averageScore}%</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-yellow-500 bg-opacity-10">
                <FaCalendar className="h-6 w-6 text-yellow-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-500">Time Spent</p>
                <p className="text-2xl font-semibold text-gray-700">{stats.timeSpent}h</p>
              </div>
            </div>
          </div>
        </div>

        {/* Assessments Section */}
        <div className="bg-white rounded-lg shadow">
          <div className="p-6">
            <h2 className="text-lg font-medium text-gray-900 mb-6">Available Assessments</h2>
            
            {error && (
              <div className="mb-4 p-4 bg-red-50 border-l-4 border-red-500 rounded-r-lg">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            )}

            {loading ? (
              <div className="flex justify-center items-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {assessments.map((assessment) => (
                  <AssessmentCard 
                    key={assessment._id} 
                    assessment={assessment}
                    submissions={submissions}
                  />
                ))}
                {assessments.length === 0 && (
                  <div className="col-span-full text-center py-8 text-gray-500">
                    No assessments available at the moment
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Results Section */}
        <AssessmentResults submissions={allSubmissions} />
      </div>
    </div>
  );
};

export default StudentDashboard;
