import React from 'react';
import { FaCheckCircle, FaClock, FaMedal } from 'react-icons/fa';

const AssessmentResults = ({ submissions }) => {
  const sortedSubmissions = [...submissions].sort((a, b) => 
    new Date(b.createdAt) - new Date(a.createdAt)
  );

  return (
    <div className="bg-white rounded-lg shadow mt-8">
      <div className="p-6">
        <h2 className="text-lg font-medium text-gray-900 mb-6">Assessment History</h2>
        
        {sortedSubmissions.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No assessments completed yet
          </div>
        ) : (
          <div className="space-y-6">
            {sortedSubmissions.map((submission) => (
              <div 
                key={submission._id} 
                className="border rounded-lg p-4 hover:shadow-md transition-shadow duration-200"
              >
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900">
                      {submission.assessment.title}
                    </h3>
                    <div className="mt-2 space-y-2 text-sm text-gray-600">
                      <div className="flex items-center">
                        <FaMedal className="mr-2 text-blue-500" />
                        Level: {submission.assessment.level.charAt(0).toUpperCase() + submission.assessment.level.slice(1)}
                      </div>
                      <div className="flex items-center">
                        <FaClock className="mr-2 text-green-500" />
                        Time Spent: {Math.round(submission.timeSpent / 60)} minutes
                      </div>
                      <div className="flex items-center">
                        <FaCheckCircle className="mr-2 text-purple-500" />
                        Score: {submission.correctAnswers} / {submission.totalQuestions} questions
                      </div>
                    </div>
                  </div>
                  <div className="flex flex-col items-end">
                    <div className={`text-lg font-bold ${
                      submission.score >= 70 ? 'text-green-600' :
                      submission.score >= 50 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {submission.score}%
                    </div>
                    <div className="text-sm text-gray-500 mt-2">
                      {new Date(submission.createdAt).toLocaleDateString()} at{' '}
                      {new Date(submission.createdAt).toLocaleTimeString()}
                    </div>
                    <div className="text-sm text-gray-500">
                      Attempt #{submission.attemptNumber}
                    </div>
                  </div>
                </div>
                
                <div className="mt-4 w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${
                      submission.score >= 70 ? 'bg-green-500' :
                      submission.score >= 50 ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{ width: `${submission.score}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AssessmentResults;
