import React from 'react';
import { FaCheckCircle, FaClock, FaMedal } from 'react-icons/fa';

const AssessmentResults = ({ submissions }) => {
  const sortedSubmissions = [...submissions].sort((a, b) => 
    new Date(b.createdAt) - new Date(a.createdAt)
  );

  return (
    <div className="bg-white rounded-lg shadow mt-6">
      <div className="p-4">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Assessment History</h2>
        
        {sortedSubmissions.length === 0 ? (
          <div className="text-center py-4 text-gray-500">
            No assessments completed yet
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {sortedSubmissions.map((submission) => (
              <div 
                key={submission._id} 
                className="border rounded-lg p-3 hover:shadow-sm transition-shadow duration-200"
              >
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <h3 className="text-base font-medium text-gray-900">
                      {submission.assessment.title}
                    </h3>
                    <div className="mt-1 grid grid-cols-2 gap-2 text-sm text-gray-600">
                      <div className="flex items-center">
                        <FaMedal className="mr-1.5 h-4 w-4 text-blue-500" />
                        <span className="truncate">
                          {submission.assessment.level.charAt(0).toUpperCase() + 
                           submission.assessment.level.slice(1)}
                        </span>
                      </div>
                      <div className="flex items-center">
                        <FaClock className="mr-1.5 h-4 w-4 text-green-500" />
                        <span>{Math.round(submission.timeSpent / 60)}m</span>
                      </div>
                      <div className="flex items-center">
                        <FaCheckCircle className="mr-1.5 h-4 w-4 text-purple-500" />
                        <span>{submission.correctAnswers}/{submission.totalQuestions}</span>
                      </div>
                      <div className="flex items-center justify-end">
                        <span className={`font-medium ${
                          submission.score >= 70 ? 'text-green-600' :
                          submission.score >= 50 ? 'text-yellow-600' : 'text-red-600'
                        }`}>
                          {submission.score}%
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="mt-2 flex items-center justify-between text-xs text-gray-500">
                  <span>
                    {new Date(submission.createdAt).toLocaleDateString()} at{' '}
                    {new Date(submission.createdAt).toLocaleTimeString()}
                  </span>
                  <span>Attempt #{submission.attemptNumber}</span>
                </div>

                <div className="mt-2 w-full bg-gray-100 rounded-full h-1.5">
                  <div 
                    className={`h-1.5 rounded-full ${
                      submission.score >= 70 ? 'bg-green-500' :
                      submission.score >= 50 ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{ width: `${submission.score}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AssessmentResults;
