import React from 'react';
import { FaCheckCircle, FaClock, FaMedal } from 'react-icons/fa';

const AssessmentResults = ({ submissions }) => {
  const sortedSubmissions = [...submissions]
    .filter(submission => submission.assessment) // Filter out submissions with null assessment
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

  return (
    <div className="bg-white rounded-lg shadow mt-6">
      <div className="p-4">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Assessment History</h2>

        {sortedSubmissions.length === 0 ? (
          <div className="text-center py-4 text-gray-500">
            No assessments completed yet
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
            {sortedSubmissions.map((submission) => (
              <div 
                key={submission._id} 
                className="border rounded-lg p-2.5 hover:shadow-sm transition-shadow duration-200"
              >
                <div>
                  <div className="flex justify-between items-start mb-2">
                    <h3 className="text-sm font-medium text-gray-900 truncate flex-1 mr-2">
                      {submission.assessment?.title || 'Deleted Assessment'}
                    </h3>
                    <span className={`text-sm font-medium ${
                      submission.score >= 70 ? 'text-green-600' :
                      submission.score >= 50 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {submission.score}%
                    </span>
                  </div>

                  <div className="grid grid-cols-2 gap-1.5 text-xs text-gray-600">
                    <div className="flex items-center">
                      <FaMedal className="mr-1 h-3.5 w-3.5 text-blue-500" />
                      <span className="truncate">
                        {submission.assessment?.level 
                          ? (submission.assessment.level.charAt(0).toUpperCase() + 
                             submission.assessment.level.slice(1))
                          : 'Unknown Level'}
                      </span>
                    </div>
                    <div className="flex items-center">
                      <FaClock className="mr-1 h-3.5 w-3.5 text-green-500" />
                      <span>{Math.round(submission.timeSpent / 60)}m</span>
                    </div>
                    <div className="flex items-center">
                      <FaCheckCircle className="mr-1 h-3.5 w-3.5 text-purple-500" />
                      <span>{submission.correctAnswers}/{submission.totalQuestions}</span>
                    </div>
                    <div className="flex items-center text-gray-500">
                      <span>Attempt #{submission.attemptNumber || 1}</span>
                    </div>
                  </div>

                  {submission.assessment?.engineeringField && (
                    <div className="mt-2 text-xs text-gray-500">
                      Field: {submission.assessment.engineeringField}
                    </div>
                  )}
                </div>

                <div className="mt-2 text-xs text-gray-500 truncate">
                  {new Date(submission.createdAt).toLocaleDateString()} {new Date(submission.createdAt).toLocaleTimeString()}
                </div>

                <div className="mt-1.5 w-full bg-gray-100 rounded-full h-1">
                  <div 
                    className={`h-1 rounded-full ${
                      submission.score >= 70 ? 'bg-green-500' :
                      submission.score >= 50 ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{ width: `${submission.score}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AssessmentResults;
